mapper.select:
select
  ID_, PROC_DEF_ID_, PROC_INST_ID_, EXECUTION_ID_, ACT_ID_, TASK_ID_, CALL_PROC_INST_ID_, ACT_NAME_, ACT_TYPE_, ASSIGNEE_, START_TIME_, END_TIME_, DURATION_, TENANT_ID_
from sup-activiti.ACT_HI_ACTINST


        and ID_ = #{id_}


        and PROC_DEF_ID_ = #{procDefId_}


        and PROC_INST_ID_ = #{procInstId_}


        and EXECUTION_ID_ = #{executionId_}


        and ACT_ID_ = #{actId_}


        and TASK_ID_ = #{taskId_}


        and CALL_PROC_INST_ID_ = #{callProcInstId_}


        and ACT_NAME_ = #{actName_}


        and ACT_TYPE_ = #{actType_}


        and ASSIGNEE_ = #{assignee_}


        and START_TIME_ = #{startTime_}


        and END_TIME_ = #{endTime_}


        and DURATION_ = #{duration_}


        and TENANT_ID_ = #{tenantId_}
